class LinkButton(QPushButton):
    def __init__(self, text, link, *args, **kwargs):
        super(LinkButton, self).__init__(text, *args, **kwargs)
        self.setCursor(QCursor(Qt.PointingHandCursor))
        self.setFlat(True)
        self.setMaximumHeight(20)
        self.setStyleSheet('QPushButton {text-decoration: underline; color: #00c899}')
        self.clicked.connect(lambda: webbrowser.open(link, new=0, autoraise=True))


class IconButton(QPushButton):
    def __init__(self, text, highlight=False, icon=None, success=False, *args, **kwargs):
        super(IconButton, self).__init__(QIcon(icon), text, *args, **kwargs)

        self.icon = icon
        self.highlight = highlight
        self.success = success
        self.setMinimumHeight(34)
        self.setCursor(QCursor(Qt.PointingHandCursor))
        if self.highlight:
            self.setStyleSheet('QPushButton{color: #161a1d; background-color: #00a07b; border: none; border-radius: 3px; padding: 10px;} QPushButton:hover {background-color: #00c899}')
            font = self.font()
            font.setPointSize(14)
            font.setBold(True)
            self.setFont(font)

        if self.success:
            self.setStyleSheet('QPushButton{color: #161a1d; background-color: #dfefd9; border: none; border-radius: 3px; padding: 10px;}')
            font = self.font()
            font.setPointSize(14)
            font.setBold(True)
            self.setFont(font)

        if self.icon:
            self.setIconSize(QSize(22, 22))
            self.setIcon(QIcon(self.AlphaImage()))

    def AlphaImage(self):
        if self.highlight and not self.success:
            AlphaImage = QPixmap(self.icon)
            painter = QPainter(AlphaImage)

            painter.setCompositionMode(QPainter.CompositionMode_SourceIn)
            painter.fillRect(AlphaImage.rect(), '182828')

            return AlphaImage

        else:
            return QPixmap(self.icon)


class SyncSketchInstaller(QObject):
    def showit(self):
        self.installer = installerUI()
        self.installer.installButton.clicked.connect(self.__syncsketchInstall)
        self.installer.closeButton.clicked.connect(self.__closeButton)
        self.installer.launchButton.clicked.connect(self.__launchButton)
        self.installer.show()


    def done(self):
        self.installer.launchButton.show()
        self.installer.closeButton.hide()
        self.installer.animatedGif.hide()
        self.installer.waitLabel.hide()


        if InstallOptions.upgrade == 1:
            restoreCredentialsFile()
            self.installer.upgradeInfo.setText('Upgrade Successfull')
            self.installer.subtext.setText('.')
            self.installer.upgradeInfo.setStyleSheet(
                'QLabel {color: #00a17b; font: 16pt}')
            self.installer.launchButton.hide()


        # Install the Shelf
        if InstallOptions.installShelf:
            from syncsketchGUI import install_shelf, uninstall_shelf
            uninstall_shelf()
            install_shelf()


        #Load Plugin And Autoload it
        cmds.loadPlugin('SyncSketchPlugin')
        cmds.pluginInfo('SyncSketchPlugin',  edit=True, autoload=True)

        #Create Default's for current OS
        self.createGoodDefaults()

    def createGoodDefaults(self):
        '''
        Adds default setting's to have a good starting point for the tool
        '''
        import yaml
        print('sscache: {0}'.format(Literals.SYNCSKETCH_INSTALL_PATH))
        syncsketch_cache = os.path.join(Literals.SYNCSKETCH_INSTALL_PATH, 'config', 'syncsketch_cache.yaml')
        print('sscache: {0}'.format(syncsketch_cache))

        with open(syncsketch_cache, 'r') as f:
            config = yaml.load(f)


        if Literals.PLATFORM == 'Darwin':
            config['current_preset'] = 'HD720p (OSX)'
        elif Literals.PLATFORM == 'Windows':
            config['current_preset'] = 'HD720p (Windows)'
        print(config)

        with open(syncsketch_cache, 'w') as f:
            yaml.dump(config, f)

        # Add TimeLineMenu's if they doesn't exist

    def __syncsketchInstall(self):
        self.installer.installButton.hide()
        self.installer.closeButton.hide()
        self.installer.installShelf.hide()
        self.installer.animatedGif.show()
        self.installer.waitLabel.show()
        self.myThread = installThread()
        self.connect(self.myThread, SIGNAL('finished()'), self.done)
        self.myThread.start()
 
    def __closeButton(self):
        self.installer.clean()
        self.installer.close()

    def __launchButton(self):
        self.installer.clean()
        self.installer.close()
        # Open UI
        from syncsketchGUI import standalone
        reload(standalone)

def restoreCredentialsFile():
    #We assume that User already has a previous version installed
    import syncsketchGUI.lib.user as user
    current_user = user.SyncSketchUser()
    if InstallOptions.tokenData:
        current_user.set_name(InstallOptions.tokenData['username'])
        # todo we should remove api_key
        current_user.set_token(InstallOptions.tokenData['token'])
        current_user.set_api_key(InstallOptions.tokenData['token'])
        current_user.auto_login()


def downloadFFmpegToDisc(platform=None, moveToLocation=None):
    import requests
    tmpdir = tempfile.mkdtemp()
    delete_tmpdir = True

    if platform == 'Windows':
        ffmpegBinaryPath = 'https://ffmpeg.zeranoe.com/builds/win64/static/ffmpeg-4.1-win64-static.zip'
    elif platform == 'Darwin':
        ffmpegBinaryPath = 'https://ffmpeg.zeranoe.com/builds/macos64/static/ffmpeg-4.1-macos64-static.zip'

    results = requests.get(ffmpegBinaryPath)

    ffmpegzip = os.path.join(tmpdir, 'ffmpeg.zip')
    ffmpegExtracted = os.path.join(tmpdir, 'extracted')

    with open(ffmpegzip, 'wb') as f:
        f.write(results.content)

    zip_ref = zipfile.ZipFile(ffmpegzip, 'r')
    zip_ref.extractall(ffmpegExtracted)
    zip_ref.close()

    ffmpegBinaryPath = glob.glob(os.path.join(ffmpegExtracted, '*', 'bin', 'ffmpeg*'))[0]
    ffprobeBinaryPath = glob.glob(os.path.join(ffmpegExtracted, '*', 'bin', 'ffprobe*'))[0]
    print('Moving FFMPEG from to directory: {0}'.format(moveToLocation))
    if not os.path.isdir(moveToLocation):
        os.makedirs(moveToLocation)
    os.chmod(ffmpegBinaryPath, 0o755)
    os.chmod(ffprobeBinaryPath, 0o755)
    shutil.copy(ffmpegBinaryPath, moveToLocation)
    shutil.copy(ffprobeBinaryPath, moveToLocation)


class installThread(QThread):
    '''Main Process that drives all installation'''

    def __init__(self):
        QThread.__init__(self)

    def __del__(self):
        self.wait()

    def run(self):
        self.startInstallationProcess()

    def startInstallationProcess(self):

        Literals.PLATFORM = platform.system()
        MAYA_VERSION = cmds.about(apiVersion=True) / 10000
        IN_MAYA = False
        PYTHON_PATH = ''
        MAYA_SCRIPTS_PATH = ''
        FFMPEG_PATH = ''
        PIP_PATH = ''
        tmpdir = None
        delete_tmpdir = False
        cmd = ''

        if Literals.PLATFORM == 'Windows':
            PYTHON_PATH = os.path.join(os.getenv('MAYA_LOCATION'), 'bin', 'mayapy.exe')
            MAYA_SCRIPTS_PATH = ScriptInstallPath['Windows']
            PIP_PATH = os.path.join(os.getenv('APPDATA'), 'Python', 'Scripts', 'pip2.7.exe')

        elif Literals.PLATFORM == 'Darwin':
            PYTHON_PATH = '/usr/bin/python'
            MAYA_SCRIPTS_PATH = ScriptInstallPath['Darwin']
            PIP_PATH = os.path.join(expanduser('~'), 'Library', 'Python', '2.7', 'bin', 'pip2.7')

        FFMPEG_PATH = os.path.join(MAYA_SCRIPTS_PATH, 'ffmpeg', 'bin')
        Literals.SYNCSKETCH_INSTALL_PATH = '{0}syncsketchGUI'.format(MAYA_SCRIPTS_PATH)

        print('MAYA_SCRIPTS_PATH: {0}'.format(MAYA_SCRIPTS_PATH))
        print('PYTHON_PATH: {0}'.format(PYTHON_PATH))
        print('PIP_PATH: {0}'.format(PIP_PATH))
        print('Literals.SYNCSKETCH_INSTALL_PATH: {0}'.format(Literals.SYNCSKETCH_INSTALL_PATH))



        try:
            if not INSTALL_SSGUI_ONLY:
                    # Create a temporary directory to act as a working directory if we were
                    # not given one.
                if tmpdir is None:
                    tmpdir = tempfile.mkdtemp()
                    delete_tmpdir = False

                # Save get-pip.py
                pipInstaller = os.path.join(tmpdir, 'get-pip.py')

                if Literals.PLATFORM == 'Darwin':
                    cmd = 'curl https://bootstrap.pypa.io/get-pip.py -o {0}'.format(pipInstaller).split(' ')
                    if not INSTALL_SSGUI_ONLY:
                        print('Calling shell command: {0}'.format(cmd))
                        print(subprocess.check_output(cmd))

                else:
                    # this should be using secure https, but we shoul dbe fine for now
                    # as we are only reading data, but might be a possible mid attack
                    response = urllib2.urlopen('https://bootstrap.pypa.io/get-pip.py')
                    data = response.read()
                    with open(pipInstaller, 'w') as f:
                        f.write(data)

                # Install pip
                filepath, filename = os.path.split(pipInstaller)
                sys.path.insert(0, filepath)
                if Literals.PLATFORM == 'Darwin':
                    cmd = '{0} {1} --user pip==19.2.3'.format('python2.7', pipInstaller).split(' ')
                else:
                    cmd = '{0}&{1}&--user&pip==19.2.3'.format(PYTHON_PATH, pipInstaller).split('&')
                print('Calling shell command for pip: {0}'.format(cmd))
                print(subprocess.check_output(cmd, stderr=subprocess.STDOUT))

            # Install Dependencies
            cmd = '{0}&install&--force-reinstall&--user&{1}&setuptools&pyyaml&requests[security]'.format(PIP_PATH,
                                                                                               SYNCSKETCH_API_RELEASE_PATH).split(
                '&')
            if not INSTALL_SSGUI_ONLY:
                print('Calling shell command: {0}'.format(cmd))
                print(subprocess.check_output(cmd))

            # Install SyncsketchGUI
            # * By using target, pip show won't find this package anymore

            if os.path.isdir(Literals.SYNCSKETCH_INSTALL_PATH):
                shutil.rmtree(Literals.SYNCSKETCH_INSTALL_PATH, ignore_errors=True)
                # todo: delete as well SyncsketchGUI-1.0.0.dist-info
                print('Deleting previous directory for a clean install {0} '.format(Literals.SYNCSKETCH_INSTALL_PATH))

            cmd = '{0}&install&--upgrade&--target={1}&{2}'.format(PIP_PATH, MAYA_SCRIPTS_PATH,
                                                                           SYNCSKETCH_GUI_RELEASE_PATH).split('&')
            print('Calling shell command: {0}'.format(cmd))
            print(subprocess.check_output(cmd))

            # Finally Move things in place
            # fromDir = os.path.join(tmpdir, 'syncsketchGUI')

            # shutil.move(fromDir, Literals.SYNCSKETCH_INSTALL_PATH)
            # print('Moving files to directory: {0}'.format(MAYA_SCRIPTS_PATH))

            # Download FFMPeg Binaries
            if not INSTALL_SSGUI_ONLY:
                downloadFFmpegToDisc(platform=Literals.PLATFORM, moveToLocation=FFMPEG_PATH)



            if not os.path.isdir(PluginInstallPath[Literals.PLATFORM]):
                pluginDir = PluginInstallPath[Literals.PLATFORM]
                print('creating plugin dir: {}'.format(pluginDir))
                os.makedirs(pluginDir)

            fromSource = os.path.join(Literals.SYNCSKETCH_INSTALL_PATH, 'SyncSketchPlugin.py')
            toTarget = os.path.join(PluginInstallPath[Literals.PLATFORM], 'SyncSketchPlugin.py')
            print('Copy From : {} to: {}'.format(fromSource, toTarget))
            shutil.copy(fromSource, toTarget)

        except Exception as e:
            print(e)

        finally:
            # Remove our temporary directory
            if delete_tmpdir and tmpdir:
                print('cleaning up temporary files: {0}'.format(tmpdir))
                shutil.rmtree(tmpdir, ignore_errors=True)



