def getMayaScriptPath():
    '''Deduce scripts path based on MAYA_SCRIPT_PATH env'''
    paths = os.environ['MAYA_SCRIPT_PATH']
    candidates = []
    for path in paths.split(os.pathsep):
        if 'maya/scripts' in path:
            candidates.append(path)
    return candidates[0]


def getMayaPlugInPath():
    '''Deduce scripts path based on MAYA_PLUG_IN_PATH env'''
    paths = os.environ['MAYA_PLUG_IN_PATH']
    candidates = []
    for path in paths.split(os.pathsep):
        if 'maya/plug-ins' in path:
            candidates.append(path)
    return candidates[0]


